import React from "react";

export default function Transaction({ items }) {
  const sign = items.amount > 0 ? "+" : "-";
  return (
    <li className={items.amount > 0 ? "plus" : "minus"}>
      {items.text}{" "}
      <span>
        {sign}${Math.abs(items.amount)}
      </span>
      <button className="delete-btn">x</button>
    </li>
  );
}
